RedactorX.add("plugin","counter",{translations:{en:{counter:{words:"words",chars:"chars"}}},subscribe:{"editor.insert, editor.set, editor.empty":function(){this.count()}},start:function(){this.$editor=this.app.editor.getEditor(),this.$editor.on("keyup."+this.prefix+"-plugin-counter paste."+this.prefix+"-plugin-counter",this.count.bind(this)),this.count()},stop:function(){this.$editor.off("."+this.prefix+"-plugin-counter"),this.app.statusbar.remove("words"),this.app.statusbar.remove("chars")},count:function(){var t=0,r=0,s=0,e=this.$editor.html();if(""!==(e=this._clean(e))){var i=e.split(/\s+/),a=e.match(/\s/g);t=i?i.length:0,s=a?a.length:0,r=e.length}var n={words:t,characters:r,spaces:s};this.app.broadcast("counter",n),this.app.statusbar.add("words",this.lang.get("counter.words")+": "+n.words),this.app.statusbar.add("chars",this.lang.get("counter.chars")+": "+n.characters)},_clean:function(t){return t=(t=(t=(t=(t=(t=(t=t.replace(/<\/(.*?)>/gi," ")).replace(/<(.*?)>/gi,"")).replace(/\t/gi,"")).replace(/\n/gi," ")).replace(/\r/gi," ")).replace(/&nbsp;/g,"1")).trim(),t=this.app.utils.removeInvisibleChars(t)}});